sys_variable_value.value:
(function execute(inputs, outputs) {

 var cmd = './hcli topic send'

if (inputs.topic_id) { cmd = cmd +  " --topicid " + inputs.topic_id } 
if (inputs.message) { cmd = cmd +  " --message '" + inputs.message + "'" } 
if (inputs.max_chunks) { cmd = cmd +  " --maxchunks " + inputs.max_chunks } 

for (i=0; i < inputs.sign_with.length; i++) {
  if (inputs.sign_with[i]) {
    cmd = cmd + ' --sign ' + inputs.sign_with[i]
  }
}

outputs.cmd = cmd 

})(inputs, outputs);

