sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    gr = new GlideRecord("x_snc_hhoverlay_message")
    if (current.total == 1) {
        gr.initialize()
        gr.sequence_number = current.sequence_number
        gr.message64 = current.message64
        gr.topic_id = current.topic_id
        gr.insert()
    } else {
        chunk = new GlideRecord("x_snc_hhoverlay_chunk")
        chunk.addQuery("topic_id", current.topic_id)
        chunk.addQuery("sequence_number", current.sequence_number)
        chunk.orderBy("number")
        chunk.query()

        var message64 = ""
        var counter = 0
        var success = true
        while (chunk.next()) {
			counter = counter + 1
            if (chunk.number == counter) {
                message64 += chunk.message64
				gs.info("counter " + i + " " + message64)
            } else {
                success = false
                break
            }
        }

        if (success == true) {
            gr.initialize()
            gr.sequence_number = current.sequence_number
            gr.message64 = message64
            gr.topic_id = current.topic_id
            gr.insert()
        }
    }

})(current, previous);
